mapper.select:
SELECT 

FROM h_hotel_check a

WHERE a.id = #{id}

SELECT 

FROM h_hotel_check a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.h_id = #{h.id}


		AND a.num = #{num}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.end_date = #{endDate}


		AND a.start_date = #{startDate}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM h_hotel_check a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE h_hotel_check SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
